import React from "react";
import ScoreBoard from "./components//New/Scoreboard";

const App = () => {
  const [matches, setMatches] = useState([]);

  const startMatch = (homeTeam, awayTeam) => {
    const newMatch = {
      homeTeam,
      awayTeam,
      homeScore: 0,
      awayScore: 0,
    };
    setMatches([...matches, newMatch]);
  };

  const updateScore = (index, homeScore, awayScore) => {
    const updatedMatches = [...matches];
    updatedMatches[index].homeScore = homeScore;
    updatedMatches[index].awayScore = awayScore;
    setMatches(updatedMatches);
  };

  const finishMatch = (index) => {
    const updatedMatches = matches.filter((match, i) => i !== index);
    setMatches(updatedMatches);
  };

  const getSummary = () => {
    const sortedMatches = matches.sort(
      (a, b) => b.homeScore + b.awayScore - a.homeScore - a.awayScore
    );
    return sortedMatches;
  };
  return <div>App</div>;
};

export default App;
