// Match.js
import React, { useState } from "react";
import { Button } from "@chakra-ui/react";

function Match({ match, onUpdateScore, onFinish }) {
  const [homeScore, setHomeScore] = useState(0);
  const [awayScore, setAwayScore] = useState(0);

  const handleUpdateScore = () => {
    onUpdateScore(homeScore, awayScore);
  };

  return (
    <div>
      <h2>
        {match.homeTeam} {homeScore} - {awayScore} {match.awayTeam}
      </h2>
      <label>
        Home Team Score:
        <input
          type="number"
          value={homeScore}
          onChange={(e) => setHomeScore(e.target.value)}
        />
      </label>
      <label>
        Away Team Score:
        <input
          type="number"
          value={awayScore}
          onChange={(e) => setAwayScore(e.target.value)}
        />
      </label>
      <button onClick={handleUpdateScore}>Update Score</button>
      <button onClick={onFinish}>Finish Match</button>
    </div>
  );
}

export default Match;
