// Scoreboard.js
import React, { useState } from "react";
import Match from "./Match";
import MatchSummary from "./MatchSummary";

import { Button, Heading } from "@chakra-ui/react";

function Scoreboard() {
  const [matches, setMatches] = useState([]);
  const [showSummaries, setShowSummaries] = useState(false);

  const handleStartMatch = (homeTeam, awayTeam) => {
    const newMatch = { homeTeam, awayTeam, homeScore: 0, awayScore: 0 };
    setMatches([...matches, newMatch]);
  };

  const handleUpdateScore = (index, homeScore, awayScore) => {
    const updatedMatches = [...matches];
    updatedMatches[index].homeScore = homeScore;
    updatedMatches[index].awayScore = awayScore;
    setMatches(updatedMatches);
  };

  const handleFinishMatch = (index) => {
    const updatedMatches = matches.filter((_, i) => i !== index);
    setMatches(updatedMatches);
  };

  const handleToggleSummaries = () => {
    setShowSummaries(!showSummaries);
  };

  return (
    <div>
      <Heading>Live Football World Cup Scoreboard</Heading>
      <Button onClick={handleToggleSummaries}>View Match Summaries</Button>
      {matches.map((match, index) => (
        <Match
          key={index}
          match={match}
          onUpdateScore={(homeScore, awayScore) =>
            handleUpdateScore(index, homeScore, awayScore)
          }
          onFinish={() => handleFinishMatch(index)}
        />
      ))}

      <Button onClick={() => handleStartMatch()}>Start Match</Button>
      {showSummaries && <MatchSummary matches={matches} />}
    </div>
  );
}

export default Scoreboard;
